class Farben:
    orange = (255, 140, 0)
    rot = (255, 0, 0)
    gruen = (0, 255, 0)
    schwarz = (0, 0, 0)
    weiss = (255, 255, 255)
    darkcyan = (0, 139, 139)

    @classmethod
    def get_colors(cls):
        return [cls.orange, cls.rot, cls.weiss, cls.gruen, cls.schwarz, cls.darkcyan]



